tileentity.line:
	bbb=nbt['extra:6']['usedChannels'];
	if(nbt['def:6']['id']==4353&&bbb!=undefined)
{
	aaa=nbt['def:6']['Damage'];
	if((aaa>=0&&aaa<=16)||(aaa>=20&&aaa<=36)||(aaa>=40&&aaa<=56))
	{
		if(bbb==8)return  RED+bbb+WHITE+"/"+8;
		else return  GREEN+bbb+WHITE+"/"+8;
	}
	else if((aaa>=60&&aaa<=76)||(aaa>=520&&aaa<=536))
	{
		if(bbb==32)return  RED+bbb+WHITE+"/"+32;
		else return  GREEN+bbb+WHITE+"/"+32;
	}
	else if((aaa>=540&&aaa<=556)||(aaa>=560&&aaa<=576))
	{
		if(bbb==128)return RED+bbb+WHITE+"/"+128;
		else return  GREEN+bbb+WHITE+"/"+128;
	}


}
